  <div class="node<?php if ($sticky) { print " sticky"; } ?><?php if (!$status) { print " node-unpublished"; } ?>"> 	  <?php // USER PICTURE  	    if ($picture) { /* print $picture; */ }?>    <?php // TITLE      if ($page == 0) { ?><h2 class="nodeTitle"><a href="<?php print $node_url?>"><?php print $title?></a>	      <?php global $base_url;	        if ($sticky) { print '<img src="'.base_path(). drupal_get_path('theme','forusa').'/img/sticky.gif" alt="'.t('sticky icon').'" class="sticky" />'; } ?>	      </h2><?php }; ?>    <?php /* CONTENT DIV BEGIN */ ?><div class="content">      <?php // REPRESENTATIVE IMAGE        if(($node->field_representative_image[0]['view']) && (($node->field_representative_image[0]['source']) != 'default_image_upload')): ?>          <div class="field field-type-filefield field-field-representative-image ir-repimage"><?php print $node->field_representative_image[0]['view'] ?></div>      <?php endif; ?>      <?php // METADATA        if ((!$teaser) && (!$is_front) && ($submitted)): ?>          <div class="metanode"><p><?php print t('Posted by ') .'<span class="author">'. theme('username', $node).'</span>' . t(' on ') . '<span class="date">'.format_date($node->created, 'custom', "d F Y, g:ia").'</span>'; ?></p>    	    </div>       <?php endif; ?>      <?php // TAXONOMY        if (!$teaser): ?>                  <?php if ($taxonomy_campaigns) { ?><div class="taxonomy taxonomy-campaigns"><span><?php print t('Related Campaigns: ') ?></span> <?php print $taxonomy_campaigns?></div><?php } ?>                  <?php if (($taxonomy_issues) || ($taxonomy_faiths)) { ?>            <div class="taxonomy-other">              <?php if ($taxonomy_issues) { ?><div class="taxonomy taxonomy-issues"><span><?php print t('Other Issues: ') ?></span> <?php print $taxonomy_issues?></div><?php } ?>              <?php if ($taxonomy_faiths) { ?><div class="taxonomy taxonomy-faiths"><span><?php print t('Faiths: ') ?></span> <?php print $taxonomy_faiths?></div><?php } ?>              <?php if ($taxonomy_tags) { ?><div class="taxonomy taxonomy-tags"><?php print $taxonomy_tags?></div><?php } ?>            </div>          <?php } ?>              <?php endif; ?>      <?php // CONTACT INFORMATION        if(($node->field_contact_name[0]['view'])||($node->field_contact_phone[0]['view'])||($node->field_contact_email[0]['view'])||($node->field_organization[0]['view'])): ?>        <div class="ir-contact">          <h3>Contact Information</h3>          <p><strong>Contact:</strong>            <ul>              <?php foreach ((array)$node->field_contact_name as $item) { ?>                <li><?php print $item['view'] ?></li>              <?php } ?>            </ul>          <?php if($node->field_contact_phone[0]['view']): ?><p><strong>Phone:</strong> <?php print $node->field_contact_phone[0]['view'] ?></p><?php endif; ?>          <?php if($node->field_contact_email[0]['view']): ?><p><strong>Email:</strong> <?php print $node->field_contact_email[0]['view'] ?></p><?php endif; ?>          <?php if($node->field_organization[0]['view']): ?><p><strong>Organization:</strong> <?php print $node->field_organization[0]['view'] ?></p><?php endif; ?>        </div>      <?php endif; ?>      <?php // LOCATION INFORMATION        if(($node->field_date[0]['view'])||($node->field_event_location[0]['view'])||($node->field_cost[0]['view'])): ?>        <div class="ir-event">          <div class="ir-location"><?php print $node->field_event_location[0]['view'] ?></div>          <?php foreach ((array)$node->field_date as $item) { ?>            <div class="ir-event-date">              <?php print $item['view'] ?>            </div>          <?php } ?>          <?php if($node->field_cost[0]['view']): ?>            <p class="ir-cost"><strong>Cost:</strong> <?php print $node->field_cost[0]['view'] ?></p>          <?php endif; ?>        </div>      <?php endif; ?>      <?php // EMBEDDED AUDIO        if($node->field_audio[0]['view']): ?>        <div class="field field-type-emaudio field-field-audio ir-audio">          <?php print $node->field_audio[0]['view'] ?>        </div>      <?php endif; ?>      <?php // EMBEDDED VIDEO      if($node->field_video[0]['view']): ?>        <div class="field field-type-emvideo field-field-video ir-video">          <?php print $node->field_video[0]['view'] ?>        </div>      <?php endif; ?>      <?php // MAIN CONTENT        ?>      <div class="field field-content ir-content">        <?php print $node->content['body']['#value'] ?>        <?php print $node->field_additional_info[0]['view'] ?>        <?php if($node->field_link[0]['view']): ?><p><strong>More Information:</strong> <?php print $node->field_link[0]['view'] ?></p><?php endif; ?>      </div>    <?php /* CONTENT DIV END */ ?></div>    <?php // $LINKS: FULL NODE    if (!$teaser): ?>      <?php if ($links) { ?><div class="links"><?php print $links?></div><?php }; ?>    <?php endif; ?>    <?php // LINKS: TEASER    if ($teaser): ?>      <?php if ($links) { ?><div class="linksteaser"><div class="links"><?php print $links?></div></div><?php }; ?>    <?php endif; ?>      </div>