   <div id="node-<?php print $node->nid; ?>" class="<?php print $classes; ?> node<?php if ($sticky) { print " sticky"; } ?><?php if (!$status) { print " node-unpublished"; } ?>"> 	    <?php if ($picture) { /* print $picture; */ } ?>    <?php if ($page == 0) { ?><h2 class="nodeTitle"><a href="<?php print $node_url?>"><?php print $title?></a>	<?php global $base_url;	// NO TO THE STICKY ICON	/* if ($sticky) { print '<img src="'.base_path(). drupal_get_path('theme','forusa').'/img/sticky.gif" alt="'.t('sticky icon').'" class="sticky" />'; } */ ?>	</h2><?php }; ?>    	<?php if (!$teaser): ?>	<?php if ($submitted): ?>	<?php if (!$is_front): ?>      <div class="metanode"><p><?php print t('By ') .'<span class="author';        /* determines the role of the author */        if (arg(0) == 'node' && is_numeric(arg(1)) && is_null(arg(2))) {          $node = node_load(arg(1));          $author = user_load(array('uid' => $node->uid));            if (in_array('staff', $author->roles)) {              print " author-staff";              } /*            else {              print "author-nonstaff";              } */          }        print '">'. theme('username', $node).'</span>' . t(' on ') . '<span class="date">'.format_date($node->created, 'custom', "d F Y").'</span>'; ?></p>          </div>   <?php endif; ?>  <?php endif; ?>  <?php endif; ?>    <div class="content">      <?php print $content?>    </div><?php if (!$is_front) : /* print links and terms only if NOT on front page */ ?>    <a name="bottom"></a>    <?php if (!$teaser): /* $links */ ?>    <?php if ($links) { ?><div class="links"><?php print $links?></div><?php }; ?>    <?php endif; ?>    <?php if ($teaser): /* $links as displayed with teasers */ ?>    <?php if ($links) { ?><div class="linksteaser"><div class="links"><?php print $links?></div></div><?php }; ?>    <?php endif; ?>    <?php if (!$teaser): /* taxonomy $terms */ ?>    <?php if ($terms) { ?><div class="taxonomy"><span class="tags-intro">View more posts on: </span><?php print $terms?></div><?php } ?>    <?php endif; ?><?php endif; ?>      </div>